import React, { useState } from 'react';
import { 
  LayoutDashboard, 
  FileText, 
  MessageSquare, 
  BarChart2, 
  Settings, 
  Search, 
  Bell, 
  Plus, 
  MoreHorizontal,
  ArrowUpRight,
  Users,
  Eye,
  Menu,
  X
} from 'lucide-react';
import { 
  AreaChart, 
  Area, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  ResponsiveContainer 
} from 'recharts';
import { motion, AnimatePresence } from 'framer-motion';
import { clsx, type ClassValue } from 'clsx';
import { twMerge } from 'tailwind-merge';

// --- Utils ---
function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

// --- Types ---
export interface DashboardStats {
  totalPosts: number;
  totalViews: number;
  newComments: number;
  growth: number;
}

export interface Post {
  id: string;
  title: string;
  author: string;
  category: string;
  status: 'published' | 'draft' | 'scheduled';
  views: number;
  date: string;
}

export interface AdminBlogDashboardProps {
  userName?: string;
  userAvatar?: string;
  stats?: DashboardStats;
}

// --- Mock Data ---
const chartData = [
  { name: 'Mon', visits: 4000 },
  { name: 'Tue', visits: 3000 },
  { name: 'Wed', visits: 2000 },
  { name: 'Thu', visits: 2780 },
  { name: 'Fri', visits: 1890 },
  { name: 'Sat', visits: 2390 },
  { name: 'Sun', visits: 3490 },
];

const recentPosts: Post[] = [
  { id: '1', title: 'The Future of Minimalist Design', author: 'Alex Morgan', category: 'Design', status: 'published', views: 1234, date: 'Oct 24, 2023' },
  { id: '2', title: 'Understanding React Server Components', author: 'Sarah Chen', category: 'Development', status: 'draft', views: 0, date: 'Oct 23, 2023' },
  { id: '3', title: '10 Tips for Better Typography', author: 'Alex Morgan', category: 'Design', status: 'published', views: 856, date: 'Oct 21, 2023' },
  { id: '4', title: 'Why We Migrated to TypeScript', author: 'Mike Ross', category: 'Engineering', status: 'published', views: 2431, date: 'Oct 20, 2023' },
  { id: '5', title: 'Q4 Marketing Strategy', author: 'Emily Blunt', category: 'Business', status: 'scheduled', views: 0, date: 'Oct 28, 2023' },
];

// --- Components ---

const SidebarItem = ({ icon: Icon, label, active, onClick }: { icon: any, label: string, active?: boolean, onClick?: () => void }) => (
  <button 
    onClick={onClick}
    className={cn(
      "w-full flex items-center gap-3 px-4 py-3 text-sm font-medium transition-all duration-200 group relative",
      active 
        ? "text-primary-foreground bg-primary shadow-lg shadow-primary/20" 
        : "text-muted-foreground hover:text-foreground hover:bg-secondary/50"
    )}
  >
    <Icon size={18} className={cn("transition-transform duration-200", active ? "scale-110" : "group-hover:scale-110")} />
    <span>{label}</span>
    {active && (
      <motion.div 
        layoutId="active-pill"
        className="absolute left-0 top-0 bottom-0 w-1 bg-accent" 
      />
    )}
  </button>
);

const StatCard = ({ label, value, trend, icon: Icon, delay }: { label: string, value: string, trend: string, icon: any, delay: number }) => (
  <motion.div 
    initial={{ opacity: 0, y: 20 }}
    animate={{ opacity: 1, y: 0 }}
    transition={{ delay, duration: 0.4 }}
    className="bg-card border border-border p-6 rounded-xl hover:shadow-md transition-shadow duration-300 group"
  >
    <div className="flex justify-between items-start mb-4">
      <div className="p-2 bg-secondary rounded-lg text-foreground group-hover:bg-accent group-hover:text-accent-foreground transition-colors duration-300">
        <Icon size={20} />
      </div>
      <span className="flex items-center text-xs font-semibold text-emerald-600 bg-emerald-50 px-2 py-1 rounded-full">
        <ArrowUpRight size={12} className="mr-1" />
        {trend}
      </span>
    </div>
    <h3 className="text-muted-foreground text-sm font-medium mb-1">{label}</h3>
    <p className="text-3xl font-bold tracking-tight text-foreground">{value}</p>
  </motion.div>
);

const StatusBadge = ({ status }: { status: Post['status'] }) => {
  const styles = {
    published: "bg-emerald-100 text-emerald-700 border-emerald-200",
    draft: "bg-zinc-100 text-zinc-700 border-zinc-200",
    scheduled: "bg-blue-100 text-blue-700 border-blue-200"
  };

  return (
    <span className={cn("px-2.5 py-0.5 rounded-full text-xs font-medium border capitalize", styles[status])}>
      {status}
    </span>
  );
};

export function AdminBlogDashboard({ 
  userName = "Admin User", 
  userAvatar = "https://i.pravatar.cc/150?u=admin",
  stats = { totalPosts: 124, totalViews: 45231, newComments: 18, growth: 12.5 }
}: AdminBlogDashboardProps) {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [activeTab, setActiveTab] = useState('dashboard');

  return (
    <div className="min-h-screen bg-background text-foreground flex overflow-hidden font-sans">

      {/* Mobile Sidebar Overlay */}
      <AnimatePresence>
        {!isSidebarOpen && (
           <div className="md:hidden fixed inset-0 bg-black/20 z-40" onClick={() => setIsSidebarOpen(true)} />
        )}
      </AnimatePresence>

      {/* Sidebar */}
      <motion.aside 
        initial={false}
        animate={{ width: isSidebarOpen ? 280 : 0, opacity: isSidebarOpen ? 1 : 0 }}
        className={cn(
          "fixed md:relative z-50 h-screen bg-card border-r border-border flex flex-col overflow-hidden",
          !isSidebarOpen && "md:w-0"
        )}
      >
        <div className="p-6 flex items-center gap-3">
          <div className="w-8 h-8 bg-foreground rounded-lg flex items-center justify-center text-background font-bold text-xl">
            B
          </div>
          <span className="font-bold text-xl tracking-tight">BlogFrame</span>
        </div>

        <nav className="flex-1 py-6 space-y-1">
          <SidebarItem icon={LayoutDashboard} label="Dashboard" active={activeTab === 'dashboard'} onClick={() => setActiveTab('dashboard')} />
          <SidebarItem icon={FileText} label="Posts" active={activeTab === 'posts'} onClick={() => setActiveTab('posts')} />
          <SidebarItem icon={MessageSquare} label="Comments" active={activeTab === 'comments'} onClick={() => setActiveTab('comments')} />
          <SidebarItem icon={BarChart2} label="Analytics" active={activeTab === 'analytics'} onClick={() => setActiveTab('analytics')} />
          <SidebarItem icon={Users} label="Team" active={activeTab === 'team'} onClick={() => setActiveTab('team')} />
        </nav>

        <div className="p-4 border-t border-border mt-auto">
          <div className="flex items-center gap-3 p-2 rounded-lg bg-secondary/50">
            <img src={userAvatar} alt={userName} className="w-10 h-10 rounded-full object-cover border-2 border-background" />
            <div className="flex-1 min-w-0">
              <p className="text-sm font-semibold truncate">{userName}</p>
              <p className="text-xs text-muted-foreground truncate">Editor in Chief</p>
            </div>
            <button className="p-1.5 hover:bg-background rounded-md transition-colors">
              <Settings size={16} />
            </button>
          </div>
        </div>
      </motion.aside>

      {/* Main Content */}
      <main className="flex-1 flex flex-col h-screen overflow-hidden relative">
        {/* Header */}
        <header className="h-16 border-b border-border bg-card/50 backdrop-blur-sm flex items-center justify-between px-6 sticky top-0 z-30">
          <div className="flex items-center gap-4">
            <button 
              onClick={() => setIsSidebarOpen(!isSidebarOpen)}
              className="p-2 hover:bg-secondary rounded-lg transition-colors"
            >
              {isSidebarOpen ? <Menu size={20} /> : <Menu size={20} />}
            </button>
            <div className="hidden md:flex items-center text-sm text-muted-foreground">
              <span>Admin</span>
              <span className="mx-2">/</span>
              <span className="text-foreground font-medium capitalize">{activeTab}</span>
            </div>
          </div>

          <div className="flex items-center gap-4">
            <div className="relative hidden md:block group">
              <Search size={16} className="absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground group-focus-within:text-accent transition-colors" />
              <input 
                type="text" 
                placeholder="Search..." 
                className="pl-9 pr-4 py-2 bg-secondary/50 border-transparent focus:bg-background border focus:border-accent rounded-full text-sm w-64 transition-all duration-300 outline-none"
              />
            </div>
            <button className="relative p-2 hover:bg-secondary rounded-full transition-colors">
              <Bell size={20} />
              <span className="absolute top-1.5 right-1.5 w-2 h-2 bg-destructive rounded-full border-2 border-background"></span>
            </button>
          </div>
        </header>

        {/* Dashboard Content */}
        <div className="flex-1 overflow-y-auto p-6 md:p-8 space-y-8 bg-background">

          {/* Welcome Section */}
          <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
            <div>
              <h1 className="text-3xl font-bold tracking-tight font-sans">Overview</h1>
              <p className="text-muted-foreground mt-1">Here's what's happening with your blog today.</p>
            </div>
            <button className="flex items-center gap-2 bg-foreground text-background hover:bg-foreground/90 px-5 py-2.5 rounded-full font-medium transition-all hover:shadow-lg hover:-translate-y-0.5 active:translate-y-0">
              <Plus size={18} />
              <span>New Post</span>
            </button>
          </div>

          {/* Stats Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            <StatCard icon={FileText} label="Total Posts" value={stats.totalPosts.toString()} trend="+12%" delay={0.1} />
            <StatCard icon={Eye} label="Total Views" value={stats.totalViews.toLocaleString()} trend="+8.2%" delay={0.2} />
            <StatCard icon={MessageSquare} label="Comments" value={stats.newComments.toString()} trend="+24%" delay={0.3} />
            <StatCard icon={BarChart2} label="Growth" value={`${stats.growth}%`} trend="+4.1%" delay={0.4} />
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Main Chart */}
            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.5 }}
              className="lg:col-span-2 bg-card border border-border rounded-xl p-6 shadow-sm"
            >
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-lg font-bold">Visitor Analytics</h2>
                <select className="bg-secondary/50 border-none text-sm rounded-lg px-3 py-1 outline-none">
                  <option>Last 7 days</option>
                  <option>Last 30 days</option>
                  <option>This Year</option>
                </select>
              </div>
              <div className="h-[300px] w-full">
                <ResponsiveContainer width="100%" height="100%">
                  <AreaChart data={chartData}>
                    <defs>
                      <linearGradient id="colorVisits" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="var(--accent)" stopOpacity={0.1}/>
                        <stop offset="95%" stopColor="var(--accent)" stopOpacity={0}/>
                      </linearGradient>
                    </defs>
                    <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="var(--border)" />
                    <XAxis 
                      dataKey="name" 
                      axisLine={false} 
                      tickLine={false} 
                      tick={{ fill: 'var(--muted-foreground)', fontSize: 12 }} 
                      dy={10}
                    />
                    <YAxis 
                      axisLine={false} 
                      tickLine={false} 
                      tick={{ fill: 'var(--muted-foreground)', fontSize: 12 }} 
                    />
                    <Tooltip 
                      contentStyle={{ backgroundColor: 'var(--card)', borderColor: 'var(--border)', borderRadius: '8px', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }}
                      itemStyle={{ color: 'var(--foreground)' }}
                    />
                    <Area 
                      type="monotone" 
                      dataKey="visits" 
                      stroke="var(--accent)" 
                      strokeWidth={3}
                      fillOpacity={1} 
                      fill="url(#colorVisits)" 
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
            </motion.div>

            {/* Quick Actions / Mini List */}
            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.6 }}
              className="bg-card border border-border rounded-xl p-6 shadow-sm"
            >
               <h2 className="text-lg font-bold mb-4">Top Performing</h2>
               <div className="space-y-4">
                 {[1, 2, 3].map((i) => (
                   <div key={i} className="flex items-center gap-4 group cursor-pointer">
                     <div className="w-12 h-12 bg-secondary rounded-lg flex items-center justify-center font-bold text-muted-foreground group-hover:text-accent group-hover:bg-accent/10 transition-colors">
                       {i}
                     </div>
                     <div className="flex-1 min-w-0">
                       <h4 className="font-medium truncate group-hover:text-accent transition-colors">Design Trends 2024</h4>
                       <p className="text-xs text-muted-foreground">2.4k views</p>
                     </div>
                     <div className="text-emerald-500 text-sm font-medium">+12%</div>
                   </div>
                 ))}
               </div>

               <button className="w-full mt-6 py-2 text-sm font-medium text-muted-foreground hover:text-foreground border border-dashed border-border rounded-lg hover:border-foreground transition-all">
                 View All Analytics
               </button>
            </motion.div>
          </div>

          {/* Recent Posts Table */}
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.7 }}
            className="bg-card border border-border rounded-xl overflow-hidden shadow-sm"
          >
            <div className="p-6 border-b border-border flex items-center justify-between">
              <h2 className="text-lg font-bold">Recent Posts</h2>
              <div className="flex gap-2">
                <button className="p-2 hover:bg-secondary rounded-lg transition-colors">
                   <Settings size={18} className="text-muted-foreground" />
                </button>
              </div>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full text-left">
                <thead className="bg-secondary/30 text-xs uppercase text-muted-foreground">
                  <tr>
                    <th className="px-6 py-4 font-semibold tracking-wider">Title</th>
                    <th className="px-6 py-4 font-semibold tracking-wider">Category</th>
                    <th className="px-6 py-4 font-semibold tracking-wider">Status</th>
                    <th className="px-6 py-4 font-semibold tracking-wider">Views</th>
                    <th className="px-6 py-4 font-semibold tracking-wider">Date</th>
                    <th className="px-6 py-4 font-semibold tracking-wider text-right">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-border">
                  {recentPosts.map((post) => (
                    <tr key={post.id} className="group hover:bg-secondary/20 transition-colors">
                      <td className="px-6 py-4">
                        <div className="flex flex-col">
                          <span className="font-medium text-foreground group-hover:text-accent transition-colors">{post.title}</span>
                          <span className="text-xs text-muted-foreground">{post.author}</span>
                        </div>
                      </td>
                      <td className="px-6 py-4 text-sm text-muted-foreground">
                        {post.category}
                      </td>
                      <td className="px-6 py-4">
                        <StatusBadge status={post.status} />
                      </td>
                      <td className="px-6 py-4 text-sm font-mono text-muted-foreground">
                        {post.views.toLocaleString()}
                      </td>
                      <td className="px-6 py-4 text-sm text-muted-foreground">
                        {post.date}
                      </td>
                      <td className="px-6 py-4 text-right">
                        <button className="p-2 hover:bg-secondary rounded-lg transition-colors opacity-0 group-hover:opacity-100">
                          <MoreHorizontal size={18} className="text-muted-foreground" />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </motion.div>

        </div>
      </main>
    </div>
  );
}

export default AdminBlogDashboard;
